import React, { useState } from 'react';
import book_image from "../images/book_dummy.jpg"

const BookCard = ({ book }) => {
    const [wishlist, setWishlist] = useState(() => {
        return JSON.parse(localStorage.getItem('wishlist')) || [];
    });

    const isWishlisted = wishlist.some(item => item.id === book.id);

    const toggleWishlist = () => {
        let updatedWishlist;
        if (isWishlisted) {
            updatedWishlist = wishlist.filter(item => item.id !== book.id);
        } else {
            updatedWishlist = [...wishlist, book];
        }
        setWishlist(updatedWishlist);
        localStorage.setItem('wishlist', JSON.stringify(updatedWishlist));
    };

    return (
        <div className="book-card">
            <img src={book.formats['image/jpeg']} className='book_card_image' alt={book.title} />
            <h3 className='book-card_title'>{book.title}</h3>
            <p className='book-card_author'><span className='card_author_by'>By:</span> {book.authors.map(author => author.name).join(', ')}</p>
            <p className='book-card_author'><span className='card_author_by'>Genre:</span> {book.subjects.join(', ')}</p>
            <button onClick={toggleWishlist}>
                {isWishlisted ? '❤️' : '♡'} Add to Wishlist
            </button>
        </div>
    );
};

export default BookCard;
